mapper.select:
select id, username, name, password, email, phone, expireTime, lockStatus, allowIps, createBy, createTime, editBy, editTime
from `tbl_user`
where `username` = #{username} and `password` = #{password}

select id, username, name, password, email, phone, expireTime, lockStatus, allowIps, createBy, createTime, editBy, editTime
from `tbl_user`
where id = #{id}
