mapper.select:
select ta.id, ta.name, tu1.name as owner, ta.startDate, ta.endDate
from `tbl_activity` ta
join `tbl_user` tu1 on ta.owner = tu1.id


        and ta.name like concat('%',#{name},'%')


        and tu1.name like concat('%',#{owner},'%')


        and ta.startDate >= #{startDate}


        and ta.endDate <= #{endDate}


order by ta.startDate desc
limit ${pageBegin}, ${pageSize}

select ta.id, ta.name, tu1.name as owner, tdv1.value as source, tdv2.value as state, ta.startDate, ta.endDate, ta.budgetCost, ta.description, tu2.name as createBy, ta.createTime, tu3.name as editBy, ta.editTime from `tbl_activity` ta join `tbl_user` tu1 on ta.owner = tu1.id join `tbl_dictionary_value` tdv1 on ta.source = tdv1.id join `tbl_dictionary_value` tdv2 on ta.state = tdv2.id join `tbl_user` tu2 on ta.createBy = tu2.id left outer join `tbl_user` tu3 on ta.editBy = tu3.id
and ta.name like concat('%',#{name},'%')
and tu1.name like concat('%',#{owner},'%')
and ta.startDate >= #{startDate}
and ta.endDate <= #{endDate}
order by ta.startDate desc

select count(ta.id) from `tbl_activity` ta join `tbl_user` tu1 on ta.owner = tu1.id
and ta.name like concat('%',#{name},'%')
and tu1.name like concat('%',#{owner},'%')
and ta.startDate >= #{startDate}
and ta.endDate <= #{endDate}

select ta.id, ta.name, ta.startDate, ta.endDate, tu.name as owner
from tbl_activity ta
         join tbl_user tu on ta.owner = tu.id
where ta.name like #{name}
  and ta.id not in (
    select activityId
    from tbl_clue_activity_relation
    where clueId = #{clueId}
)

select ta.id, ta.name, ta.startDate, ta.endDate, tu.name as owner
from tbl_activity ta
         join tbl_user tu on ta.owner = tu.id
where ta.name like #{name}
  and ta.id in (
    select activityId
    from tbl_clue_activity_relation
    where clueId = #{clueId}
)
